database-object.create:
CREATE TRIGGER dbo.id_gen_Worker ON dbo.Worker INSTEAD OF INSERT AS BEGIN SET NOCOUNT ON;
declare @lastval int set @lastval = (select max(Id) from Worker) if @lastval is null set @lastval = 0
SELECT * INTO #Inserted FROM Inserted UPDATE #Inserted set Id = @lastval+1 SET NOCOUNT OFF; INSERT INTO Worker SELECT * FROM #Inserted END

CREATE TRIGGER dbo.id_gen_WorkerWithExplicitKey ON dbo.WorkerWithExplicitKey INSTEAD OF INSERT AS BEGIN SET NOCOUNT ON;
declare @lastval int set @lastval = (select max(Id) from WorkerWithExplicitKey) if @lastval is null set @lastval = 0
SELECT * INTO #Inserted FROM Inserted UPDATE #Inserted set Id = @lastval+1 SET NOCOUNT OFF; INSERT INTO WorkerWithExplicitKey SELECT * FROM #Inserted END

